module.exports = {

  setupBundleAnalyzer: function(config){
    var BundleAnalyzerPlugin = require('webpack-bundle-analyzer').BundleAnalyzerPlugin
    config.plugins.push(new BundleAnalyzerPlugin({
      analyzerMode: 'server',
      analyzerPort: 9999,
      openAnalyzer: true,
      generateStatsFile: false
    }))
  }
}
